#include <iostream>#include <fstream>#include "functions.h"using namespace std;int main(){	setlocale(LC_ALL, "Russian");	functions Functions;	int temp;	bool exit{ false };	string filename = " file.exe";	while(exit != true){		cout << "1. Добавить контакт" << endl			<< "2. Показать все контакты" << endl			<< "3. Сделать резервную копию нынешнего списка контактов" << endl			<< "4. Выход" << endl << endl;		cout << "Выберите действие: ";		cin >> temp;					if (temp == 1) {			Functions.add_contact();		}		else if (temp == 2) {			Functions.watch_all_contact();		}		else if (temp == 3) {			Functions.save_contact(filename);		}		else if (temp == 4) {			exit = true;		}		else {			cerr << "Ошибка выбора действия" << endl;		}	}}